import React, {useState} from "react";
import {Button, Modal} from "react-bootstrap";
import ButtonCreate from "../ButtonCreate";
// import Modal from "../Modal";
import 'bootstrap/dist/css/bootstrap.min.css';
import './MyProducts.scss';
import {ReactSVG} from "react-svg";
import buttonIcon from "../../assets/images/button-icon.svg";
import plus from "../../assets/images/plus.svg";
import MainTitle from "../MainTitle";

function MyVerticallyCenteredModal(props) {
	return (
		<Modal
			{...props}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
			className="modal-window"
		>
			<Modal.Header closeButton>
				<Modal.Title id="contained-modal-title-vcenter">
					Create a product
				</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Store"/>
				</div>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Pirce"/>
				</div>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Product name"/>
				</div>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Product Category"/>
				</div>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Quantity of goods"/>
				</div>
				<div>
					<input type="text" className="modal__input" value="" placeholder="Weight / Volume of one item"/>
				</div>
			</Modal.Body>
			<Modal.Footer>
				<Button className="modal__btn" onClick={props.onHide}>
					<span className="modal__btn-text">Add product</span>
					<ReactSVG className="modal__btn-icon" src={plus} />
				</Button>
			</Modal.Footer>
		</Modal>
	);
}

const MyProducts = (props) => {
	const [modalShow, setModalShow] = React.useState(false);

	const myProductsProps = {
		title: "My product",
		description: "Product table"
	}

	return (
		<div className="wrap">
			<div className="wrap__top">
				<MainTitle title={myProductsProps.title} description={myProductsProps.description}/>
				<Button className="button" variant="primary" onClick={() => setModalShow(true)}>
					<ButtonCreate />
				</Button>
				{/*<Modal />*/}
				<MyVerticallyCenteredModal
					show={modalShow}
					onHide={() => setModalShow(false)}
				/>
			</div>
			<hr/>
			<div className="wrap__content">
				<div className="graphs">
					<div className="graph-1 graph">

					</div>
					<div className="graph-2 graph">

					</div>
					<div className="graph-3 graph">

					</div>
				</div>
			</div>
		</div>
	)
}

export default MyProducts;
