import React from "react";
import {Button, Modal} from "react-bootstrap";

import ButtonCreate from "../ButtonCreate";
import MyCreateModal from "../MyCreateModal";
import MainTitle from "../MainTitle";

import 'bootstrap/dist/css/bootstrap.min.css';
import './MainPage.scss';
import {mainPageProps, modalCreate} from "../../services/mock";
import ChartPie from "../Charts/ChartPie";
import ChartBar from "../Charts/ChartBar";
import ChartLine from "../Charts/ChartLine/ChartLine";




const MainPage = () => {
	const [modalCreateShow, setModalCreateShow] = React.useState(false);
	const getSellProd = JSON.parse(localStorage.getItem('sellProducts'))

	const getTotalPrice = (day) => {
		let total = 0;
		getSellProd.reduce((prev, current) => {
			if(current.saleDate === day) {
				total = prev + +current.price * +current.sellQuantity
				return total
			}
			total = prev
			return total
		}, 0)
		return total
	}

	const getMonTotal = getTotalPrice('Mon');
	const getTueTotal = getTotalPrice('Tue');
	const getWedTotal = getTotalPrice('Wed');
	const getThuTotal = getTotalPrice('Thu');
	const getFriTotal = getTotalPrice('Fri');
	const getSatTotal = getTotalPrice('Sat');
	const getSunTotal = getTotalPrice('Sun');


	const dataGraphs = [
		{ day: 'Mon', total: getMonTotal },
		{ day: 'Tue', total: getTueTotal },
		{ day: 'Wed', total: getWedTotal },
		{ day: 'Thu', total: getThuTotal },
		{ day: 'Fri', total: getFriTotal },
		{ day: 'Sat', total: getSatTotal },
		{ day: 'Sun', total: getSunTotal },
	];

	return (
		<div className="wrap">
			<div className="wrap__top">
				<MainTitle title={mainPageProps.title} description={mainPageProps.description}/>
				<Button className="button" variant="primary" onClick={() => setModalCreateShow(true)}>
					<ButtonCreate />
				</Button>

			</div>
			<hr/>
			<div className="wrap__content">
				<div className="graphs">
					<div className="graph-1 graph">
						{/*<div className="no-data">*/}
						{/*	<p>No data</p>*/}
						{/*</div>*/}
						<p className="graph__title">Sales schedule by day</p>
						<ChartPie />
					</div>
					<div className="graph-2 graph">
						{/*<div className="no-data">*/}
						{/*	<p>No data</p>*/}
						{/*</div>*/}
						<p className="graph__title">Total earned</p>
						<ChartLine />
					</div>
					<div className="graph-3 graph">
						{/*<div className="no-data">*/}
						{/*	<p>No data</p>*/}
						{/*</div>*/}
						<p className="graph__title">Sales Overview</p>
						<p className="graph__subtitle">Graph sales for all days</p>
						<ChartBar data={dataGraphs}/>
					</div>
				</div>
				{modalCreateShow && (
					<MyCreateModal
						info={modalCreate}
						show={modalCreateShow}
						onHide={() => setModalCreateShow(false)}
					/>
				)}
			</div>
		</div>
	)
}

export default MainPage;
