import * as React from 'react';
import Paper from '@material-ui/core/Paper';
import {
	Chart,
	PieSeries,
	Legend,
	Title,
} from '@devexpress/dx-react-chart-material-ui';
import { Animation } from '@devexpress/dx-react-chart';
import './ChartPie.scss';

const data = [
	{ country: 'Russia', area: 12 },
	{ country: 'Canada', area: 7 },
	{ country: 'USA', area: 7 },
	{ country: 'Others', area: 55 },
];

const ChartLine = () => {
	const getSellProd = JSON.parse(localStorage.getItem('sellProducts'))
	const getChartPieData = getSellProd.map((prod) => {
		return {
			saleDate: prod.saleDate,
			sellQuantity: prod.sellQuantity,
		}
	})

	console.log('========>getChartPieData', getChartPieData);

	const [dataPie, setDataPie] = React.useState(data)
	return (
		<Paper>
			<Chart
				data={dataPie}
				width="200"
				height="200"
				rotated='true'
			>
				<PieSeries
					valueField="area"
					argumentField="country"
				/>
				<Legend />
				<Animation/>
			</Chart>
		</Paper>
	)
}

export default ChartLine;
