import './MyModal.scss';
import {ReactSVG} from "react-svg";
import plus from '../../assets/images/plus.svg';
import {Button, Modal} from "react-bootstrap";
import React, {useState} from "react";


const MyModal = (
	{
		info,
		getValues,
		editValues,
		onHide,
		show,
	}) => {
	const [form, setFrom] = useState({});
	console.log('========>form', form);
	const handleChange = (e, type) => {
		const {value} = e.target
	}
	return (
		<Modal
			onHide={onHide}
			show={show}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
			className="modal-window"
		>
			<form className="modal-form">
				<Modal.Header closeButton>
					<Modal.Title id="contained-modal-title-vcenter">
						{info.title}
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					{info.inputs.map((input) => (
						<div>
							<input
								name={input.name}
								type={input.type}
								onChange={(e) => handleChange(e, input.name)}
								className="modal__input"
								placeholder={input.placeholder}
							/>
						</div>
					))}
				</Modal.Body>
				<Modal.Footer>
					<Button type="submit" className="modal__btn" onClick={(e) => {
						e.preventDefault();
						onHide()
						if (getValues) getValues()
						if (editValues) editValues()
					}}>
						<span className="modal__btn-text">{info.buttonText}</span>
						<ReactSVG className="modal__btn-icon" src={info.buttonIcon}/>
					</Button>
				</Modal.Footer>
			</form>
		</Modal>
	)
}

export default MyModal;
