import * as React from 'react';
import Paper from '@material-ui/core/Paper';
import {
	Chart,
	BarSeries,
	Title,
	ArgumentAxis,
	ValueAxis,
} from '@devexpress/dx-react-chart-material-ui';
import { Animation } from '@devexpress/dx-react-chart';
import './ChartBar.scss';



const ChartBar = () => {
	const getSellProd = JSON.parse(localStorage.getItem('sellProducts'))

	const getTotalPrice = (day) => {
		let prev = 0;
		getSellProd.reduce((prev, current) => {
			if(current.saleDate === day) {
				return prev + +current.price * +current.sellQuantity
			}
			return prev
		})
		return prev
	}

	const getMonTotal = getTotalPrice('Mon')


	const data = [
		{ day: 'Mon', population: getMonTotal },
		{ day: 'Tue', population: 3.018 },
		{ day: 'Wed', population: 3.682 },
		{ day: 'Thu', population: 4.440 },
		{ day: 'Fri', population: 5.310 },
		{ day: 'Sat', population: 6.127 },
		{ day: 'Sun', population: 800 },
	];
	console.log('========>getMonTotal', getMonTotal);

	const getChartBarData = getSellProd.map((prod) => {
		return {
			price: +prod.price,
			saleDate: prod.saleDate,
		}
	})

	return (
		<Paper>
			<Chart
				data={getChartBarData}
				height="400"
			>
				<ArgumentAxis />
				<ValueAxis max={1000} />

				<BarSeries
					valueField="price"
					argumentField="saleDate"
				/>
				<Animation />
			</Chart>
		</Paper>
	)
}

export default ChartBar;
