	// 	}
	// ]
	localStorage.setItem('products', JSON.stringify(products))
	localStorage.setItem('sellProducts', JSON.stringify(sellProducts))

	return (
		<BrowserRouter>
			{isLogin ? (
				<Register/>
			) : (
				<div className="page">
					<div className="sidebar">
						<Sidebar/>
					</div>
					<div className="main">
						<Switch>
							{routes.map((route) => (
								<Route
									component={route.component}
									path={route.path}
									exact={route.exact}
								/>
							))}
						</Switch>
					</div>
				</div>
			)}
		</BrowserRouter>
	);
}

export default App;
