import React, {useEffect, useState} from "react";
import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";

import {routes} from "./routes/routes";
import Sidebar from "./components/Sidebar";
import Register from "./components/Register";
import Login from "./components/Login";

import './App.scss';
import "./assets/fonts/fonts.css";


const App = () => {
	const products = JSON.parse(localStorage.products || []);
	const sellProducts = JSON.parse(localStorage.sellProducts || []);
	const users = JSON.parse(localStorage.users || []);
	const [isLogin, setIsLogin] = useState(JSON.parse(localStorage.getItem('isLogin')) || false)
	const [isReg, setIsReg] = useState(JSON.parse(localStorage.getItem('isReg')) || false)

	// const users = [
	// 	{
	// 		id: '0',
	// 		firstName: 'Denis',
	// 		lastName: 'Pereloma',
	// 		companyName: 'Justice',
	// 		productCategory: 'IT',
	// 		address: 'Taganrog',
	// 		email: 'qqq@mail.ru',
	// 		password: 'qwerty'
	// 	},
	// 	{
	// 		id: '1',
	// 		firstName: 'D',
	// 		lastName: 'P',
	// 		companyName: 'J',
	// 		productCategory: 'IT',
	// 		address: 'T',
	// 		email: 'q@mail.ru',
	// 		password: 'qwerty'
	// 	}
	// ]

	useEffect(() => {
		localStorage.setItem('products', JSON.stringify(products))
		localStorage.setItem('sellProducts', JSON.stringify(sellProducts))
		localStorage.setItem('users', JSON.stringify(users))
		localStorage.setItem('isReg', JSON.stringify(isReg));
		localStorage.setItem('isLogin', JSON.stringify(isLogin));
	}, [])

	const changeIsLoin = (value) => {
		setIsLogin(value)
	}

	const changeIsReg = (value) => {
		setIsReg(value)
	}

	return (
		<BrowserRouter>
			{!isReg
				? <Register changeIsReg={changeIsReg}/>
				: !isLogin
					? <Login changeIsLoin={changeIsLoin}/>
				: <div className="page">
					<div className="sidebar">
						<Sidebar/>
					</div>
					<div className="main">
						<Switch>
							{routes.map((route) => (
								<Route
									component={route.component}
									path={route.path}
									exact={route.exact}
								/>
							))}
						</Switch>
					</div>
				</div>}
			)
		</BrowserRouter>
	);
}

export default App;
