import React, {useState} from "react";

import {Button} from "react-bootstrap";
import {NavLink, Redirect} from "react-router-dom";

import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.scss';
import FormInput from "../FormInput";


const Login = ({changeIsLoin}) => {
	const [getUsers, setGetUsers] = useState(JSON.parse(localStorage.getItem('users')))
	const [loginForm, setLoginForm] = useState({});
	const [isLogin, setIsLogin] = useState(JSON.parse(localStorage.getItem('isLogin')));
	const handleChange = (e, type) => {
		const {value} = e.target
		setLoginForm((prevForm) => ({
			...prevForm,
			[type]: value,
		}))
	}

	console.log('========>loginForm', loginForm);
	return (
		<div className="wraps">
			{isLogin && <Redirect to={'/'}/>}
			<div className="wrap-left">
				<h1>Sign in</h1>
				<form className="register-form">
					<div className="register-form__row">
							<div className="register-form__item">
								<label className="register-form__item-title" htmlFor="email">Email</label>
								<input className="register-form__item-input"
											 type="email"
											 id="email"
											 name="email"
											 placeholder="Email"
											 value={loginForm.email}
											 onChange={(e) => handleChange(e, "email")}
								/>
							</div>
					</div>
					<div className="register-form__row">
							<div className="register-form__item">
								<label className="register-form__item-title" htmlFor="password">Password</label>
								<input className="register-form__item-input"
											 type="password"
											 id="password"
											 name="password"
											 placeholder="Enter password"
											 value={loginForm.password}
											 onChange={(e) => handleChange(e, "password")}
								/>
							</div>
					</div>
					<Button className="register-form__btn" onClick={(e) => {
						// getUsers.forEach((user) => {
						// 	console.log('========>user', user);
						// })
						// let result = [];
						// for (let i = 0; i <= getUsers.length; i++) {
						// 	for (let [key, value] in getUsers[i]) {
						// 		// if(key === 'email') {
						// 		// 	console.log('========>value', value);
						// 		// }
						// 		if(getUsers[i][key].indexOf('email') != -1) {
						// 			console.log('========>getUsers[i][key]', getUsers[i][key]);
						// 		}
						// 	}
						// }
						var objects = [
							{
								"foo" : "bar",
								"bar" : "sit"
							},
							{
								"foo" : "lorem",
								"bar" : "ipsum"
							},
							{
								"foo" : "dolor",
								"bar" : "amet"
							}
						];

						var results = [];

						var toSearch = "lo";

						for(var i=0; i<objects.length; i++) {
							for(let key in objects[i]) {
								if(objects[i][key].indexOf(toSearch)!=-1) {
									results.push(objects[i]);

								}
							}
						}

						console.log('========>results', results);
						setIsLogin(true);
						localStorage.setItem('isLogin', JSON.stringify(true));
						changeIsLoin(true)
					}}>
						<div className="register-form__button">
							<span className="register-form__btn-text">Log in</span>
						</div>
					</Button>
				</form>
				<div className="wrap-left__question">
					<NavLink className="wrap-left__question-link" to="/register">Forgot password?</NavLink>
				</div>
			</div>
			<div className="wrap-right">
			</div>
		</div>
	)
}

export default Login;
