import './MyModal.scss';
import {ReactSVG} from "react-svg";
import plus from '../../assets/images/plus.svg';
import {Button, Modal} from "react-bootstrap";
import React from "react";



const MyModal = (props) => {
	const getValuesInputs = () => {
		let inputs = document.querySelectorAll('.modal__input');
		let values = {};
		inputs.forEach((input) => {
			let name = input.name;
			values[name] = input.value;
		});
		products.push(values);
		localStorage.setItem('products', JSON.stringify(products))
	}
	return (
		<Modal
			{...props}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
			className="modal-window"
		>
			<form className="modal-form">
				<Modal.Header closeButton>
					<Modal.Title id="contained-modal-title-vcenter">
						{props.info.title}
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					{props.info.inputs.map((input) => (
						<div>
							<input name={input.name} type={input.type} className="modal__input" placeholder={input.placeholder}/>
						</div>
					))}
				</Modal.Body>
				<Modal.Footer>
					<Button type="submit" className="modal__btn" onClick={() => {
						props.onHide()
						getValuesInputs()
					}}>
						<span className="modal__btn-text">{props.info.buttonText}</span>
						<ReactSVG className="modal__btn-icon" src={props.info.buttonIcon} />
					</Button>
				</Modal.Footer>
			</form>
		</Modal>
	)
}

export default MyModal;
