import plus from '../assets/images/plus.svg';

export const modalCreate = {
	title: 'Create a product',
	buttonIcon: {plus},
	buttonText: 'Add product',
	inputs: [
		{
			value: '',
			type: 'text',
			placeholder: 'Store',
			name: 'storeName',
		},
		{
			value: '',
			type: 'text',
			placeholder: 'Price',
			name: 'price',
		},
		{
			value: '',
			type: 'text',
			placeholder: 'Product name',
			name: 'productName',
		},
		{
			value: '',
			type: 'text',
			placeholder: 'Product Category',
			name: 'productCategory',
		},
		{
			value: '',
			type: 'text',
			placeholder: 'Quantity of goods',
			name: 'quantity',
		},
		{
			value: '',
			type: 'text',
			placeholder: 'Weight / Volume of one item',
			name: 'weight',
		},
	]
}

export const modalSell = {
	title: 'Sell the product',
	buttonIcon: '',
	buttonText: 'Sell product',
	inputs: [
		{
			type: 'text',
			placeholder: 'Numbers of product'
		},
		{
			type: 'text',
			placeholder: 'Date of sale'
		},
	]
}

export const modalEdit = {
	title: 'Editing a product',
	buttonIcon: '',
	buttonText: 'Save changes',
	inputs: [
		{
			type: 'text',
			placeholder: 'Store',
		},
		{
			type: 'text',
			placeholder: 'Price'
		},
		{
			type: 'text',
			placeholder: 'Product name'
		},
		{
			type: 'text',
			placeholder: 'Product Category'
		},
		{
			type: 'text',
			placeholder: 'Quantity of goods'
		},
		{
			type: 'text',
			placeholder: 'Weight / Volume of one item'
		},
	]
}

export const mainPageProps = {
	title: "Sales statistics",
	description: "Welcome to CRM dashboard"
}

export const myProductsProps = {
	title: "My product",
	description: "Product table"
}

export const mySalesProps = {
	title: "My sales",
	description: "Sales table"
}

export const tableMyProductTitles = [
	'Product name',
	'Store',
	'Address',
	'Category',
	'Creation date',
	'Price',
	'Remains',
	'Weight / Volume',
	'Actions'
]
