import React, {useEffect, useState} from "react";
import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";

import {routes} from "./routes/routes";
import Sidebar from "./components/Sidebar";
import Register from "./components/Register";
import Login from "./components/Login";

import './App.scss';
import "./assets/fonts/fonts.css";
import MainPage from "./components/MainPage";
import MySales from "./components/MySales";
import MyProducts from "./components/MyProducts";
import Personal from "./components/Personal";

const App = () => {
	const [isToken, setIsToken] = useState(localStorage.getItem('token') || '');
	const [isReg, setIsReg] = useState(false)


	const removeToken = () => {
		setIsToken('')
		localStorage.setItem('token', JSON.stringify(''));
	}

	const createToken = (response) => {
		setIsToken(response.token)
		localStorage.setItem('token', response.token);
	}

	const changeIsReg = (value) => {
		setIsReg(value)
	}
	console.log('========>isReg', isReg);
	console.log('========>isToken', isToken);
	return (
		<BrowserRouter>
			{/*// !isReg*/}
			{/*// ? <Register changeIsReg={changeIsReg}/>*/}
			{/*// : !isToken*/}
			{/*// 	? <Login createToken={createToken}/>*/}
			{/*// 	:*/}
			{/*	// <Switch>*/}
			{/*	// 	{routes.map((route) => {*/}
			{/*	// 			const Main = route.component;*/}
			{/*	// 			return <Route*/}
			{/*	// 				render={() => <Main changeIsReg={changeIsReg} removeToken={removeToken}/>}*/}
			{/*	// 				path={route.path}*/}
			{/*	// 				exact={route.exact}*/}
			{/*	// 			/>*/}
			{/*	// 		}*/}
			{/*	// 	)}*/}
			{/*	// </Switch>*/}
			<Route
				render={() => <Register removeToken={removeToken}/>}
				// component={Register}
				path="/register"
				exact="true"
			/>
			<Route
				render={() => <Login removeToken={removeToken} createToken={createToken} isToken={isToken}/>}
				// component={Login}
				path="/login"
				exact="true"
			/>
			<Route
						 render={() => isToken ? <MainPage removeToken={removeToken}/> :
							 <Redirect to="/register"/>}
				// 		 component={MainPage}
						 path="/"
						 exact="true"
			/>
			<Route
						 render={() => isToken ? <MySales removeToken={removeToken}/> :
							 <Redirect to="/register"/>}
				// component={MySales}
						 path="/my-sales"
						 exact="true"
			/>
			<Route
						 render={() => isToken ? <MyProducts removeToken={removeToken}/> :
							 <Redirect to="/register"/>}
				// component={MyProducts}
						 path="/my-products"
						 exact="true"
			/>
			<Route
						 render={() => isToken ? <Personal removeToken={removeToken}/> :
							 <Redirect to="/register"/>}
				// component={Personal}
						 path="/personal"
						 exact="true"
			/>


		</BrowserRouter>
	);
}

export default App;
