import React, {useCallback, useEffect, useState} from "react";
import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";

import {routes} from "./routes/routes";
import Sidebar from "./components/Sidebar";
import Register from "./components/Register";
import Login from "./components/Login";

import './App.scss';
import "./assets/fonts/fonts.css";
import MainPage from "./components/MainPage";
import MySales from "./components/MySales";
import MyProducts from "./components/MyProducts";
import Personal from "./components/Personal";
import {getAll, getAllSales, getOneUser} from "./services/ajaxUser";

const App = () => {
	const [isToken, setIsToken] = useState(localStorage.getItem('token') || {token: ''});
	const [getUser, setGetUser] = useState({})
	const [getProd, setGetProd] = useState([])
	const [getSaleProd, setGetSaleProd] = useState([])

	const removeToken = () => {
		setIsToken('')
		localStorage.setItem('token', JSON.stringify(''));
	}

	const createToken = (response) => {
		setIsToken(response.token)
		localStorage.setItem('token', {token: response.token});
	}

	const getProductsCall = useCallback(
		() => {
			getAll()
				.then(data => {
					setGetProd(data)
				})
		}, [])

	const getMyUser = useCallback(
		() => {
			getOneUser()
				.then(data => {
					setGetUser(data)
				})
		}, [])

	const getMySales = useCallback(
		() => {
			getAllSales()
				.then(data => {
					setGetSaleProd(data)
				})
		}, [])

	useEffect(() => {
		getProductsCall()
		getMyUser()
		getMySales()
	}, [])


	// console.log('========>isToken', isToken);
	return (
		<BrowserRouter>
			{/*// !isReg*/}
			{/*// ? <Register changeIsReg={changeIsReg}/>*/}
			{/*// : !isToken*/}
			{/*// 	? <Login createToken={createToken}/>*/}
			{/*// 	:*/}
			{/*	// <Switch>*/}
			{/*	// 	{routes.map((route) => {*/}
			{/*	// 			const Main = route.component;*/}
			{/*	// 			return <Route*/}
			{/*	// 				render={() => <Main changeIsReg={changeIsReg} removeToken={removeToken}/>}*/}
			{/*	// 				path={route.path}*/}
			{/*	// 				exact={route.exact}*/}
			{/*	// 			/>*/}
			{/*	// 		}*/}
			{/*	// 	)}*/}
			{/*	// </Switch>*/}
			<Route
				render={() => <Register removeToken={removeToken}/>}
				path="/register"
				exact="true"
			/>
			<Route
				render={() => <Login removeToken={removeToken} createToken={createToken} isToken={isToken}/>}
				path="/login"
				exact="true"
			/>
			<Route
				render={() => !isToken
					? <Redirect to="/register"/>
					: <MainPage getUser={getUser}
										getProd={getSaleProd}
										setGetProd={setGetSaleProd}
										setGetUser={setGetUser}
										removeToken={removeToken}/>
					}
				path="/"
				exact="true"
			/>
			<Route
				render={() => isToken
					? <MySales getUser={getUser}
									 getProd={getSaleProd}
									 setGetProd={setGetSaleProd}
									 setGetUser={setGetUser}
									 removeToken={removeToken}/>
					: <Redirect to="/register"/>}
				path="/my-sales"
				exact="true"
			/>
			<Route
				render={() => isToken
					? <MyProducts getUser={getUser}
											getProd={getProd}
											setGetProd={setGetProd}
											setGetUser={setGetUser}
											removeToken={removeToken}/>
					: <Redirect to="/register"/>}
				path="/my-products"
				exact="true"
			/>
			<Route
				render={() => isToken
					? <Personal getUser={getUser}
										getProd={getProd}
										setGetProd={setGetProd}
										setGetUser={setGetUser}
										removeToken={removeToken}/>
					: <Redirect to="/register"/>}
				path="/personal"
				exact="true"
			/>
		</BrowserRouter>
	);
}

export default App;
