import React, {useState} from "react";
import {ReactSVG} from "react-svg";
import {Button, Modal} from "react-bootstrap";

import './MyModal.scss';

const MyModal = (
	{
		info,
		onHide,
		show,
	}) => {
	const [form, setFrom] = useState({
		id: Date.now(),
		createDate: new Date(),
	});

	const handleChange = (e, type) => {
		const {value} = e.target
		setFrom((prevForm) => ({
			...prevForm,
			[type]: value,
		}))
	}

	console.log('========>form', form);

	return (
		<Modal
			onHide={onHide}
			show={show}
			size="lg"
			aria-labelledby="contained-modal-title-vcenter"
			centered
			className="modal-window"
		>
			<form className="modal-form">
				<Modal.Header closeButton>
					<Modal.Title id="contained-modal-title-vcenter">
						{info.title}
					</Modal.Title>
				</Modal.Header>
				<Modal.Body>
					{info.inputs.map((input) => (
						<div>
							<input
								name={input.name}
								type={input.type}
								onChange={(e) => handleChange(e, input.name)}
								className="modal__input"
								placeholder={input.placeholder}
								value={form[input.name]}
							/>
						</div>
					))}
				</Modal.Body>
				<Modal.Footer>
					<Button type="submit" className="modal__btn" onClick={(e) => {
						e.preventDefault();
						onHide()
						const getProd = JSON.parse(localStorage.getItem('products'))
						getProd.push(form);
						localStorage.setItem('products', JSON.stringify(getProd))
					}}>
						<span className="modal__btn-text">{info.buttonText}</span>
						<ReactSVG className="modal__btn-icon" src={info.buttonIcon}/>
					</Button>
				</Modal.Footer>
			</form>
		</Modal>
	)
}

export default MyModal;
